<script>
  import Menu from './Menu'
  import axios  from 'axios'
  export default {
    data: () => ({
      hodimlar: [],
      bemorlar: []
    }),
    components: {
      Menu
    },
    created() {
      axios.get('http://localhost:3000/hodim').then(response => {
        this.hodimlar = response.data
      })
      axios.get('http://localhost:3000/bemor').then(response => {
        this.bemorlar = response.data
      })
    },
    computed: {
      hodimlarCount(){
        return this.hodimlar.length
      },
      bemorlarCount(){
        return this.bemorlar.length
      }
    }
  }
</script>

